mapper.insert:
		INSERT 
		  INTO MEMBER
		VALUES ( 
				 #{ id }
		       , #{ pwd }
		       , #{ name }
		       , #{ nickname }
		       , #{ email }
		       , #{ sex }
		       , #{ phone }
		       , #{ address }
		       , sysdate
		       , sysdate
		       , default		
      	       )

mapper.update:
UPDATE MEMBER SET
PWD = #{ pwd },
NAME = #{ name }, NICKNAME = #{ nickname }, EMAIL = #{ email }, PHONE = #{ phone }, ADDRESS = #{ address }, UPDATEDATE = SYSDATE WHERE ID = #{ id }

mapper.select:
SELECT
       *
  FROM MEMBER
 WHERE NAME = #{ name }
   AND EMAIL = #{ email }

SELECT
       *
  FROM MEMBER
 WHERE ID = #{ id }
   AND EMAIL = #{ email }
