mapper.select:

SELECT
       *
  FROM MEMBER
 WHERE ID = #{ id }
   AND PWD = #{pwd}
   AND MSTATUS='Y'

mapper.insert:
		INSERT 
		  INTO MEMBER
		VALUES ( 
				 #{ id }
		       , #{ pwd }
		       , #{ name }
		       , #{ nickname }
		       , #{ email }
		       , #{ gender }
		       , #{ age }
		       , #{ phone }
		       , #{ address }
		       , sysdate
		       , sysdate
		       , default		
      	       )

mapper.update:
UPDATE 
       MEMBER
   SET 
       PWD = #{ pwd }
     , NAME = #{ name }
     , EMAIL = #{ email }
     , AGE = #{ age }
     , PHONE = #{ phone }
     , ADDRESS = #{ address }
     , UPDATEDATE = SYSDATE
 WHERE ID = #{ id }
