mapper.update:
update qa
set qa_readcount = qa_readcount + 1
where qa_id = #{ qa_id }

update qa
set qa_title = #{ qa_title },
	qa_content = #{ qa_content }
	
	, qa_origin_file_name = null,
	qa_rename_file_name = null
	
	
	, qa_origin_file_name = #{ qa_origin_file_name },
	qa_rename_file_name = #{ qa_rename_file_name }
	
	where qa_id = #{ qa_id }

update qa
set qa_status = 'N'
where qa_id = #{ qa_id }
